const saveApiDataToContext = (state, value) => {
  return { ...state, videos: [...value] };
};

const changeCategory = (state, value) => {
  return { ...state, category: value };
};

const addToPlaylist = (state, value) => {
  return [...state, { [value]: [] }];
};

const addToWatchLater = () => {
  return { ...state, watchlater: [ state.watchlater ] };
};

export { saveApiDataToContext, changeCategory, addToPlaylist, addToWatchLater };
