import { useState } from "react";
import { useVideos } from "../../context/videos-context";

const NoteCard = ({ currentVideoId }) => {
  const { videoState } = useVideos();
  return (
    <>
      <AddNote />
      {videoState.notes
        .filter(({ videoId }) => videoId === currentVideoId)
        .map((item) => (
          <SmallNoteComponent noteData={item} />
        ))}
    </>
  );
};

const SmallNoteComponent = ({ noteData }) => {
  const { title } = noteData;
  return (
    <>
      <div className="m-y-2 p-y-1 p-x-1 center-x small-note-comp br-3">
        <i className="bx is-4 bx-edit-alt m-r-1"></i>{" "}
        <span className="is-3">{title}</span>{" "}
        <i className="bx is-4 bx-trash-alt m-l-1"></i>
      </div>
    </>
  );
};

const AddNote = ({}) => {
  const [showAddNotes, setShowNotes] = useState(false);
  const { videoState, videoDispatch } = useVideos();
  const [noteData, setNoteData] = useState({ title: "", content: "" });
  const saveClickHandler = () => {
    videoDispatch({ type: "ADD_NOTE", value: { currentVideoId, noteData } });
  };
  return (
    <>
      {!showAddNotes && <div>Add note</div>}
      
    </>
  );
};

export { NoteCard };
