import { useState } from "react";
import { useVideos } from "../../context/videos-context";
import { inList } from "../../utils/in-list";

const AddToWatchLater = ({ videoData }) => {
  const inWatchlater = inList(videoState.watchlater, videoData._id);
  const [buttonText, setButtonText] = useState("Add to Watch Later");
  const { videoState, videoDispatch } = useVideos();
  const clickHandler = () => {
    if (inWatchlater) {
      videoDispatch({
        type: "REMOVE_FROM_WATCHLATER",
        value: videoData._id,
      });
      setButtonText("Add to Watch Later");
    } else {
      videoDispatch({ type: "ADD_TO_WATCHLATER", value: videoData._id });
      setButtonText("Remove from Watch Later");
    }
  };
  return (
    <button onClick={clickHandler} className="btn-secondary m-dw-1 btn-small">
      <i className="bx bx-bookmark-plus m-r-1"></i>
      {buttonText}
    </button>
  );
};

export { AddToWatchLater };
