import { useState } from "react";
import { useVideos } from "../../context/videos-context";

const NoteCard = ({ currentVideoId }) => {
  const { videoState, videoDispatch } = useVideos();
  const [noteData, setNoteData] = useState({ title: "", content: "" });
  const saveClickHandler = () => {
    videoDispatch({ type: "ADD_NOTE", value: { currentVideoId, noteData } });
  };
  return (
    <>
      <div className="custom-form">
        <div className="form notes-width center-x m-up-6 has-accent br-3">
          <div className="form-div">
            <p className="form-label is-3 semibold">Notes</p>
            <i className="fa-solid fa-note-sticky"></i>
            <input
              type="text"
              onChange={(e) =>
                setNoteData({ ...noteData, title: e.target.value })
              }
              className="form-input input-focused"
              placeholder="Title"
              required
            />
          </div>
          <div className="form-div">
            <textarea
              name=""
              onChange={(e) =>
                setNoteData({ ...noteData, content: e.target.value })
              }
              className="form-textarea"
              placeholder="Description"
              id=""
            ></textarea>
          </div>
          <button
            onClick={saveClickHandler}
            className="center-x btn-secondary btn-small"
          >
            Save
          </button>
        </div>
      </div>
      {videoState.notes
        .filter(({ videoId }) => videoId === currentVideoId)
        .map((item) => (
          <SmallNoteComponent noteData={item} />
        ))}
    </>
  );
};

const SmallNoteComponent = ({ noteData }) => {
  const { title } = noteData;
  return (
    <>
      <div className="m-y-2 p-y-1 p-x-1 center-x small-note-comp br-3">
        
        <span className="is-3">{title}</span>{" "}
        <i className="bx bx-trash-alt"></i>
      </div>
    </>
  );
};

export { NoteCard };
