import { useParams } from "react-router-dom";
import { useVideos } from "../../../context/videos-context";
import { CardLikes } from "../../card/card-likes";
import { AddToWatchLater } from "../../card/add-to-watchlater";
import { AddToPlaylistModal } from "../../modals/add-to-playlist-modal";

const SingleVideoPage = () => {
  const [showModal, setShowModal] = useState(false);
  const switchShowModal = () => {
    set
  }
  const { id } = useParams();
  const { videoState } = useVideos();
  const videoData = videoState.videos.find(({ _id }) => _id === id);
  const { videoLink } = videoData;
  return (
    <div className="single-video-page width-100">
      {showModal && (
        <AddToPlaylistModal
          videoId={videoData._id}
          switchModal={switchShowModal}
        />
      )}
      <div className="single-videopage-video p-up-4 p-l-4">
        <div className="videowrapper">
          <iframe
            // src={`${videoLink}?modestbranding=1&showinfo=0&autoplay=1`}
            src={`${videoLink}`}
            title="YouTube video player"
            frameborder="0"
            controls="0"
            autohide="1"
            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
          ></iframe>
        </div>
      </div>
      <div className="video-page-content">
        <div className="video-page-buttons m-up-2 flex-r-w width-100">
          <CardLikes videoData={videoData} />
          <div className="btn-vertical">
            <AddToWatchLater videoData={videoData} />
          </div>
        </div>
      </div>
    </div>
  );
};
export { SingleVideoPage };
