import { useState } from "react";
import { useVideos } from "../../context/videos-context";

const NoteCard = ({ currentVideoId }) => {
  const { videoState } = useVideos();
  return (
    <>
      <AddNote currentVideoId={currentVideoId} />
      {videoState.notes[currentVideoId] &&
        videoState.notes[currentVideoId].map((item) => (
          <SmallNoteComponent
            currentVideoId={currentVideoId}
            currentNoteData={item}
          />
        ))}
    </>
  );
};

const SmallNoteComponent = ({ currentNoteData, currentVideoId }) => {
  const { videoDispatch } = useVideos();
  const [showEditNotes, setEditNotes] = useState(false);
  const toggleShowEditNotes = () => {
    setEditNotes((prevState) => !prevState);
  };
  const { title } = currentNoteData;
  const deleteNoteClickHandler = () => {
      vid
  };
  return (
    <>
      {!showEditNotes && (
        <div
          onClick={toggleShowEditNotes}
          className="m-y-2 p-y-1 p-x-1 center-x small-note-comp br-3"
        >
          <i className="bx is-4 bx-edit-alt m-r-1"></i>{" "}
          <span className="is-3">{title}</span>{" "}
          <i
            onClick={deleteNoteClickHandler}
            className="bx is-4 bx-trash-alt m-l-1"
          ></i>
        </div>
      )}
      {showEditNotes && (
        <EditNote
          currentNoteData={currentNoteData}
          toggleShowEditNotes={toggleShowEditNotes}
          currentVideoId={currentVideoId}
        />
      )}
    </>
  );
};

const EditNote = ({ currentVideoId, currentNoteData, toggleShowEditNotes }) => {
  const { videoState, videoDispatch } = useVideos();
  const { noteId } = currentNoteData;
  const [noteData, setNoteData] = useState({
    title: currentNoteData.title,
    content: currentNoteData.content,
  });
  const saveClickHandler = () => {
    videoDispatch({
      type: "EDIT_NOTE",
      value: { noteId, currentVideoId, noteData },
    });
    toggleShowEditNotes();
  };
  return (
    <>
      <div className="custom-form">
        <div className="form notes-width center-x m-up-6 has-accent br-3">
          <div className="form-div custom-form-form-div">
            <i
              onClick={toggleShowEditNotes}
              className="form-close pointer fas fa-times"
            ></i>
            <p className="form-label is-3 semibold">Notes</p>
            <i className="fa-solid fa-note-sticky"></i>
            <input
              type="text"
              value={noteData.title}
              onChange={(e) =>
                setNoteData({ ...noteData, title: e.target.value })
              }
              className="form-input input-focused"
              placeholder="Title"
              required
            />
          </div>
          <div className="form-div">
            <textarea
              name=""
              value={noteData.content}
              onChange={(e) =>
                setNoteData({ ...noteData, content: e.target.value })
              }
              className="form-textarea"
              placeholder="Description"
              id=""
            ></textarea>
          </div>
          <button
            onClick={saveClickHandler}
            className="center-x btn-secondary btn-small"
          >
            Save
          </button>
        </div>
      </div>
    </>
  );
};

const AddNote = ({ currentVideoId }) => {
  const [showAddNotes, setShowNotes] = useState(false);
  const toggleShowAddNotes = () => {
    setShowNotes((prevState) => !prevState);
  };
  const { videoState, videoDispatch } = useVideos();
  const [noteData, setNoteData] = useState({ title: "", content: "" });
  const saveClickHandler = () => {
    videoDispatch({ type: "ADD_NOTE", value: { currentVideoId, noteData } });
    toggleShowAddNotes();
  };
  return (
    <>
      {!showAddNotes && <div onClick={toggleShowAddNotes}>Add note</div>}
      {showAddNotes && (
        <div className="custom-form">
          <div className="form notes-width center-x m-up-6 has-accent br-3">
            <div className="form-div custom-form-form-div">
              <i
                onClick={toggleShowAddNotes}
                className="form-close pointer fas fa-times"
              ></i>
              <p className="form-label is-3 semibold">Notes</p>
              <i className="fa-solid fa-note-sticky"></i>
              <input
                type="text"
                onChange={(e) =>
                  setNoteData({ ...noteData, title: e.target.value })
                }
                className="form-input input-focused"
                placeholder="Title"
                required
              />
            </div>
            <div className="form-div">
              <textarea
                name=""
                onChange={(e) =>
                  setNoteData({ ...noteData, content: e.target.value })
                }
                className="form-textarea"
                placeholder="Description"
                id=""
              ></textarea>
            </div>
            <button
              onClick={saveClickHandler}
              className="center-x btn-secondary btn-small"
            >
              Save
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export { NoteCard };
