import { useState } from "react";
import { useVideos } from "../../context/videos-context";

const Search = () => {
  const { videoState } = useVideos();
  const [searchText, setSearchText] = useState("");
  let [searchedProducts, setSearchedProducts] = useState();

  const searchInputHandler = (text) => {
    setSearchText(text);
    const filteredProducts = videoState.videos.filter(({title})=>title.includes(searchText).map(i))
  };
  return (
    <div className="form-div nav-search">
      <i className="fas fa-search is-primary" />
      <input
        onChange={(e) => searchInputHandler(e.target.value)}
        type="text"
        className="form-input br-3"
        placeholder="search"
      />
      <div className="search-results">{searchedProducts}</div>
    </div>
  );
};

export { Search };
